import React, { Component } from 'react'

class Form extends Component {
  constructor(props) {
    super(props)
    this.state = {
      form: {
        firstNoun: "",
        firstVerb: "",
        firstAdjective: "",
        lastNoun: "",
        lastVerb: "",
        lastAdjective: ""
      }
    }
  }

  handleChange = (e) => {
    console.log(e.target.name)
    const { form } = this.state
    form[e.target.name] = e.target.value
    this.setState({ form: form })
  }

  handleSubmit = (e) => {
    e.preventDefault()
    console.log("submitted!")
    this.props.handleFormSubmit(this.state.form)
  }

  handleClear = (e) => {
    const { form } = this.state
    e.preventDefault()
    console.log("clearing!")
    this.setState({ form: null })
    this.props.handleFormSubmit(form)
  }

  render(){
    // console.log(this.state.form)
    return(
      <>
        <h3></h3>
        <form>
          <label>Enter your first noun:</label>
          <input
            name="firstNoun"
            type="text"
            value={ this.state.form.firstNoun }
            onChange={ this.handleChange }
          />
          <br />
          <label>Enter your first verb:</label>
          <input
            name="firstVerb"
            type="text"
            value={ this.state.form.firstVerb }
            onChange={ this.handleChange }
          />
          <br />
          <label>Enter your first Adjective:</label>
          <input
            name="firstAdjective"
            type="text"
            value={ this.state.form.firstAdjective }
            onChange={ this.handleChange }
          />
          <br />
          <label>Enter your last noun:</label>
          <input
            name="lastNoun"
            type="text"
            value={ this.state.form.lastNoun }
            onChange={ this.handleChange }
          />
          <br />
          <label>Enter your last verb:</label>
          <input
            name="lastVerb"
            type="text"
            value={ this.state.form.lastVerb }
            onChange={ this.handleChange }
          />
          <br />
          <label>Enter your last Adjective:</label>
          <input
            name="lastAdjective"
            type="text"
            value={ this.state.form.lastAdjective }
            onChange={ this.handleChange }
          />
          <br />
          <input
            type="submit"
            value="Submit"
            onClick={ this.handleSubmit }
          />
          <br />
          <input
            type="submit"
            value="Clear"
            onClick={ this.handleClear }
          />
        </form>
      </>
    )
  }
}

export default Form
